
CREATE TABLE size (idtalla int primary key auto_increment not null, nombre varchar(200));
CREATE TABLE category (idcategoria int primary key auto_increment not null, nombre varchar(200));
CREATE TABLE products (idproducto int primary key auto_increment not null, nombre varchar(200), descripcion varchar(200), precio int, enlace varchar(200), talla int, foreign key (talla) references size (idtalla), categoria int, foreign key (categoria) references category (idcategoria));

